import React, { useState } from 'react';
import Card from 'react-bootstrap/Card'

function TodoForm(props) {

  // constructor(props) {
  //   super(props);
  //   this.state = { item: {} };
  // }

  const [tasks, setTasks] = useState({item: {}});

  handleInputChange = e => {
    setTasks({ item: {...tasks, [e.target.name]: e.target.value } });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    e.target.reset();
    this.props.handleSubmit(tasks);
    const item = {};
    setTasks(item);
  };

    return (
      <Card>
        <h3>Add Item</h3>
        <form onSubmit={this.handleSubmit}>
          <label>
            <span>To Do Item</span>
            <input
              name="text"
              placeholder="Add To Do List Item"
              onChange={this.handleInputChange}
            />
          </label>
          <label>
            <span>Difficulty Rating</span>
            <input defaultValue="1" type="range" min="1" max="5" name="difficulty" onChange={this.handleInputChange} />
          </label>
          <label>
            <span>Assigned To</span>
            <input type="text" name="assignee" placeholder="Assigned To" onChange={this.handleInputChange} />
          </label>
          <button>Add Item</button>
        </form>
      </Card>
    );
}

export default TodoForm;
